const express = require('express');
const router = express.Router();
const Berita = require('../models/berita'); // Asumsi model 'Berita' sudah ada

router.get('/', (req, res) => {
    Berita.find()
      .sort({ tanggal: -1 })
      .then(berita => {
        console.log(berita);  
        res.render('beranda', { berita: berita });
      })
      .catch(err => {
        console.log(err);
        res.send('Error fetching news');
      });
  });
  

// Halaman Create Berita
router.get('/create', (req, res) => {
    res.render('create');
});

// Menyimpan berita baru
router.post('/create', async (req, res) => {
    try {
        const { judul, isi, tanggal } = req.body;
        const berita = new Berita({ judul, isi, tanggal });
        await berita.save();
        res.redirect('/');
    } catch (err) {
        res.status(500).send("Terjadi kesalahan dalam menyimpan berita");
    }
});

// Halaman Edit Berita
router.get('/edit/:id', async (req, res) => {
    try {
        const berita = await Berita.findById(req.params.id);
        res.render('edit', { berita: berita });
    } catch (err) {
        res.status(500).send("Berita tidak ditemukan");
    }
});

// Mengupdate berita
router.post('/edit/:id', async (req, res) => {
    try {
        const { judul, isi, tanggal } = req.body;
        await Berita.findByIdAndUpdate(req.params.id, { judul, isi, tanggal });
        res.redirect('/');
    } catch (err) {
        res.status(500).send("Terjadi kesalahan dalam mengupdate berita");
    }
});

// Menghapus berita
router.get('/delete/:id', async (req, res) => {
    try {
        await Berita.findByIdAndDelete(req.params.id);
        res.redirect('/');
    } catch (err) {
        res.status(500).send("Terjadi kesalahan dalam menghapus berita");
    }
});

module.exports = router;
